import * as React from 'react'
import * as ReactDOM from 'react-dom';
import styles from './styles.module.css';
import { CloseSquareOutlined, RedoOutlined, UndoOutlined, ZoomInOutlined, ZoomOutOutlined } from '@ant-design/icons';

interface Props {
  src?: string,
  fallback?: string,
  width?: number,
  height?: number,
  onError?: React.EventHandler<any>,
}

let placeholder = "data:image/png;base64,";

export const ImageViewComponent = ({ src, width, height, fallback }: Props) => {

  const [visible, setVisible] = React.useState(false);
  const [err, setErr] = React.useState(false);

  const callback = React.useCallback(() => {
    setVisible(false);
  }, []);

  function imgClickHandle() {
    if (!err) {
      setVisible(true);
    }
  }

  return <div>
    <img
      src={src}
      style={{ width, height }}
      onError={(e) => {
        if (e.target instanceof HTMLImageElement) {
          e.target.src = fallback || placeholder;
          setErr(true);
        }
      }}
      onClick={imgClickHandle}
    />
    <ImageViewer visible={visible} url={src} onClose={callback} />
  </div>

}


const ImageViewer = React.memo(({ visible, url, onClose }: { visible: boolean, url: string | undefined, onClose: () => void }) => {

  const [zoom, setZoom] = React.useState(0);
  const [rotate, setRotate] = React.useState(0);

  function zoomIn() {
    setZoom(v => v + 0.05);
  }

  function zoomOut() {
    setZoom(v => v > 0.1 ? v - 0.05 : v);
  }

  function rotateLeft() {
    setRotate(v => v + 90)
  }

  function rotateRight() {
    setRotate(v => v - 90)
  }

  function closeHandle() {
    onClose();
    setZoom(0);
    setRotate(0);
  }

  if (visible) {
    return ReactDOM.createPortal(
      <div className={styles.imageWrapper}>
        <div className={styles.imageBox}>
          <img alt="图片" id="custom-img" onLoad={() => {
            let img = document.getElementById("custom-img");
            if (img) {
              let zoom = window.innerHeight * 0.8 / img.clientHeight;
              setZoom(zoom);
            }
          }} onClick={closeHandle} src={url} style={{ transition: "0.5s", transform: `scale(${zoom}) rotate(${rotate}deg)` }} />
        </div>
        <CloseSquareOutlined onClick={closeHandle} className={styles.closeIcon} />
        <div className={styles.funcBox}>
          <ZoomInOutlined className={styles.funcIcon} onClick={zoomIn} />
          <ZoomOutOutlined className={styles.funcIcon} onClick={zoomOut} />
          <RedoOutlined className={styles.funcIcon} onClick={rotateLeft} />
          <UndoOutlined className={styles.funcIcon} onClick={rotateRight} />
        </div>
      </div>,
      document.body
    )
  } else {
    return null;
  }
})

export default ImageViewComponent;